import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {


  title = 'todo-app';
  windowItems: any[] = [];

  addWindow() {
    if ((this.windowItems.length === 0)) {
      this.windowItems.push({});
      console.log(this.windowItems)
    }
  }
}
<div class="app-container">
  <h1>To Do Application</h1>
  <div id="uncompleted" class="uncompleted">
    <h2>Uncompleted Tasks</h2>
    <app-uncompleted></app-uncompleted>
    <button (click)="addWindow()">Add a next task</button>
  </div>
  <div >
    <div>
   <div>
  <app-list [windowItems]="windowItems" (clearWindowEvent)="clearWindow()"></app-list>
</div>



  </div>
  <div id="completed" class="completed">
    <h2>Completed Tasks</h2>
    <app-completed></app-completed>
  </div>
</div>
