import { Component } from '@angular/core';
import { TaskService } from '../task.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  constructor(private taskService: TaskService) {}

  windowItems: any[] = [];

  addWindow() {
    if (this.windowItems.length === 0) {
      this.windowItems.push({});
      console.log(this.windowItems);
    }
  }

  clearWindow() {
    this.windowItems = [];
  }

  addTask(taskName: string, priority: string) {
    const initialCompletedValue = "uncompleted"; 
    this.taskService.addTask(taskName, priority, initialCompletedValue);
    this.clearWindow();
  }
}
<div>
  <button (click)="addWindow()">Add a next task</button>
  <div class="input-container" *ngFor="let item of windowItems">
    <div id="window" (clearEvent)="clearWindow()">
      <input class="name-input" type="text" placeholder="Task name" #taskNameInput required>

      <div class="priority">
        <p>Priority</p>
        <select
          class="priority-select"
          name="priority"
          id="priority"
          #prioritySelect
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <button
          class="add-task-button"
          (click)="addTask(taskNameInput.value, prioritySelect.value)"
          [disabled]="!taskNameInput.value"
        >
          Add Task
        </button>
      </div>
    </div>
  </div>
</div>
