import { Component } from '@angular/core';
import { TaskService } from "../task.service";

@Component({
  selector: 'app-high-priority',
  templateUrl: './high-priority.component.html',
  styleUrls: ['./high-priority.component.css']
})
export class HighPriorityComponent {
  constructor(private taskService: TaskService) {}

  get uncompletedItems() {
    return this.taskService.getUncompletedTasks();
  }

  getUncompletedTasksWithHighPriority(priority: string) {
    return this.tasks.filter((task) => task.completed === false && task.priority === "high");
  }
}
