import { Component } from '@angular/core';
import { TaskService } from "../task.service";

@Component({
  selector: 'app-high-priority',
  templateUrl: './high-priority.component.html',
  styleUrls: ['./high-priority.component.css']
})
export class HighPriorityComponent {
  uncompletedHighPriorityTasks: any[] = [];

  constructor(private taskService: TaskService) {
    this.uncompletedHighPriorityTasks = this.taskService.getUncompletedTasksWithHighPriority('high');
  }
}
<div>
  <h2>High priority tasks:</h2>
  <ul>
    <li *ngFor="let task of uncompletedHighPriorityTasks">
      {{ task.name }} - {{ task.priority }}
    </li>
  </ul>
  <p>
    <strong>{{ task.name }}</strong>
    ({{ task.priority }})
  </p>
  <div class="buttons-container">
    <button (click)="completion(task)">Done</button>
    <button (click)="deleteTask(task.id)">Delete</button>
  </div>
</div>
